import React, { useRef, useState, useEffect } from 'react';
import { Swiper, SwiperSlide } from 'swiper/react';
import { useNavigate, useLocation, useParams } from 'react-router-dom';
import 'swiper/css';
import 'swiper/css/free-mode';
import { FreeMode } from 'swiper/modules';
import menuData from "../../data/services.json";
import './ServicesMenu.css';

const ServicesMenu = () => {
    const [fixed, setFixed] = useState(false);
    const wrapperMenuRef = useRef(null);
    const swiperRef = useRef(null);
    const [activeIndex, setActiveIndex] = useState(null);
    const sectionRefs = useRef({});
    const navigate = useNavigate();
    const location = useLocation();
    const { id } = useParams();

    useEffect(() => {
        const handleScroll = () => {
            const block = wrapperMenuRef.current;
            const blockTop = block.getBoundingClientRect().top;
            const firstBlockHeight = document.querySelector(".header-wrapper .content").offsetHeight;
            setFixed(blockTop < firstBlockHeight);

            let currentSection = null;
            Object.keys(sectionRefs.current).forEach(key => {
                const section = sectionRefs.current[key];
                const rect = section.getBoundingClientRect();
                if (rect.top <= 120 && rect.bottom >= 120) {
                    currentSection = key;
                }
            });

            if (currentSection !== null && currentSection !== activeIndex) {
                setActiveIndex(Number(currentSection));
                navigate(`/service-${currentSection}`, { replace: true });
            }
        };

        const handleTouchStart = handleScroll;

        window.addEventListener('scroll', handleScroll);
        window.addEventListener('touchstart', handleTouchStart);

        return () => {
            window.removeEventListener('scroll', handleScroll);
            window.removeEventListener('touchstart', handleTouchStart);
        };
    }, [activeIndex, navigate]);

    useEffect(() => {
        const observerOptions = {
            root: null,
            rootMargin: '-110px 0px -99% 0px',
            threshold: [0, 1],
        };

        const observer = new IntersectionObserver((entries) => {
            entries.forEach(entry => {
                if (entry.isIntersecting) {
                    const id = entry.target.getAttribute('id').split('-')[1];
                    setActiveIndex(Number(id));
                    navigate(`/service-${id}`, { replace: true });
                    scrollToSlide(id);
                }
            });
        }, observerOptions);

        menuData.forEach(service => {
            const element = document.getElementById(`service-${service.id}`);
            if (element) {
                observer.observe(element);
                sectionRefs.current[service.id] = element;
            }
        });

        return () => {
            menuData.forEach(service => {
                const element = document.getElementById(`service-${service.id}`);
                if (element) {
                    observer.unobserve(element);
                }
            });
        };
    }, [navigate]);

    useEffect(() => {
        if (id) {
            const element = document.getElementById(`service-${id}`);
            if (element) {
                element.scrollIntoView({ behavior: 'smooth', block: 'start' });
                setActiveIndex(Number(id));
                scrollToSlide(Number(id));
            }
        }
    }, [id]);

    const scrollToSlide = (index) => {
        if (swiperRef.current && swiperRef.current.swiper) {
            swiperRef.current.swiper.slideTo(index - 1, 1000, false);
        }
    };

    const handleClick = (index) => {
        setActiveIndex(index);
        navigate(`/service-${index}`);
        scrollToSlide(index);
    };

    return (
        <div className='servicesmenu-wrapper' ref={wrapperMenuRef}>
            <div className={`fallback ${fixed ? 'fixed' : ''}`} />
            <div className={`servicesmenu${fixed ? " fixed" : ""}`} style={{ top: fixed ? `${document.querySelector(".header-wrapper .content").offsetHeight}px` : '' }}>
                <div className='content'>
                    <Swiper
                        slidesPerView={'auto'}
                        freeMode={true}
                        modules={[FreeMode]}
                        centeredSlides={true}
                        centeredSlidesBounds={true}
                        ref={swiperRef}
                    >
                        {menuData.map(service => (
                            <SwiperSlide className={`item`} key={service.id}>
                                <div
                                    onClick={() => handleClick(service.id)}
                                    className={`${activeIndex === service.id ? 'active-service' : ''}`}
                                >
                                    {service.name}
                                </div>
                            </SwiperSlide>
                        ))}
                    </Swiper>
                </div>
            </div>
        </div>
    );
};

export default ServicesMenu;
