import ServicesMenu from '../ServicesMenu/ServicesMenu';

import { Swiper, SwiperSlide } from 'swiper/react';
import './Services.css';
import 'swiper/css/free-mode';

import detailingServices from "../../data/services.json";
import { FreeMode } from 'swiper/modules';

function Services() {
    const breakpoints = {
        // Когда ширина экрана меньше 640px
        640: {
          slidesPerView: 1,
        },
        // Когда ширина экрана меньше 768px
        768: {
          slidesPerView: 2,
        },
        // Когда ширина экрана меньше 1024px
        1024: {
          slidesPerView: 3,
        },
      };

    return (
        <div className='services-container'>
            <ServicesMenu />
            {detailingServices.flatMap(category =>
                category.services.map(service => (
                    <SwiperSlide key={service.name} className={'service-container service-id-' + category.id} >
                        <div className='content'>
                            <h3>{service.name}</h3>
                            <p>{service.description}</p>
                            <p>Цена: ${service.price}</p>
                            <img src={service.url} alt={service.name} style={{ maxWidth: '100%', height: 'auto' }} />
                        </div>
                    </SwiperSlide>
                ))
            )}
        </div>
    );
}

export default Services;
