import React, { useState, useEffect, useRef } from 'react';
import { Swiper, SwiperSlide } from 'swiper/react';
import './ServicesMenu.css';
import 'swiper/css';
import 'swiper/css/free-mode';
import menuData from "../../data/services.json";

function ServicesMenu({ setActiveIndex }) {
    const [fixed, setFixed] = useState(false);
    const menuRef = useRef(null);

    useEffect(() => {
        const handleScroll = () => {
            const block = menuRef.current;
            const blockTop = block.getBoundingClientRect().top;

            const firstBlockHeight = document.querySelector(".header-wrapper .content").offsetHeight;
            setFixed(blockTop < firstBlockHeight);
        };

        window.addEventListener('scroll', handleScroll);

        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    }, []);

    const handleItemClick = (index) => {
        setActiveIndex(index);
        const activeElement = menuRef.current.querySelector(`.service-id-${index}`);
        if (activeElement) {
            const containerRect = menuRef.current.getBoundingClientRect();
            const elementRect = activeElement.getBoundingClientRect();
            const offset = elementRect.left - containerRect.left - (containerRect.width - elementRect.width) / 2;
            menuRef.current.scrollTo({
                left: menuRef.current.scrollLeft + offset,
                behavior: 'smooth'
            });
        }
    };

    return (
        <div className='servicesmenu-wrapper'>
            <div className={"fallback"} style={{ display: fixed ? "block" : 'none' }}></div>
            <div className={'servicesmenu' + (fixed ? " fixed" : "")} ref={menuRef}>
                <Swiper
                    slidesPerView={"auto"}
                    freeMode={true}
                    className='content'
                >
                    {menuData.map(service => (
                        <SwiperSlide key={service.id} className={`item service-id-${service.id}`} onClick={() => handleItemClick(service.id)}>{service.name}</SwiperSlide>
                    ))}
                </Swiper>
            </div>
        </div>
    );
}

export default ServicesMenu;
