import React, { useRef, useState, useEffect } from 'react';
import { Swiper, SwiperSlide } from 'swiper/react';

import './ServicesMenu.css';
import 'swiper/css';
import 'swiper/css/free-mode';

import menuData from "../../data/services.json";
import { FreeMode } from 'swiper/modules';

let blockHeight;
let firstBlockHeight;

function ServicesMenu() {
    const [fixed, setFixed] = useState(false);
    const [activeBlock, setActiveBLock] = useState(0);
    const [activeIndex, setActiveIndex] = useState(null);

    useEffect(() => {
        const observerOptions = {
            root: null,
            rootMargin: '0px 0px -100% 0px', // Проверяем пересечение только с верхней границей
            threshold: 0,
        };

        const observerCallback = (entries) => {
            entries.forEach((entry) => {
                if (entry.isIntersecting) {
                    const index = parseInt(entry.target.getAttribute('data-index'), 10);
                    setActiveIndex(index);
                }
            });
        };

        const observer = new IntersectionObserver(observerCallback, observerOptions);

        const sections = document.querySelectorAll('.services .service-category');
        sections.forEach((section, index) => {
            section.setAttribute('data-index', index);
            observer.observe(section);
        });

        const handleScroll = () => {
            const block = document.querySelector(".servicesmenu-wrapper");
            const blockTop = block.getBoundingClientRect().top;
            blockHeight = block.offsetHeight;

            firstBlockHeight = document.querySelector(".header-wrapper .content").offsetHeight;
            //console.log("blockTop=" + blockTop, "firstBlockHeight=" + firstBlockHeight);
            if (blockTop < firstBlockHeight) {
                setFixed(true);
            } else {
                setFixed(false);
            }


        };

        const handleLoad = () => {
            window.addEventListener('scroll', handleScroll);
        };

        handleLoad();

        return () => {
            window.removeEventListener('scroll', handleScroll);
            sections.forEach((section) => {
                observer.unobserve(section);
            });
        };
    }, []);

    return (
        <div className='servicesmenu-wrapper'>
            <div className={"fallback"}
                style={{
                    display: fixed ? "block" : 'none',
                    height: blockHeight
                }}
            ></div>
            <div className={'servicesmenu' + (fixed ? " fixed" : "")}
                style={{
                    position: fixed ? 'fixed' : 'inherit',
                    top: fixed ? firstBlockHeight + 'px' : ''
                }}
            >
                <Swiper
                    slidesPerView={"auto"}
                    freeMode={true}
                    modules={[FreeMode]}
                    className='content'
                >
                    {menuData.map((service, index) => (
                        <SwiperSlide key={service.id} className={`item ` + index}>{service.name}</SwiperSlide>
                    ))}
                </Swiper>
            </div>
        </div>
    );
}

export default ServicesMenu;
