import ServicesMenu from '../ServicesMenu/ServicesMenu';

import { Swiper, SwiperSlide } from 'swiper/react';
import './Services.css';
import 'swiper/css/grid';
import 'swiper/css/free-mode';
import 'swiper/css/pagination';

import detailingServices from "../../data/services.json";
import { FreeMode, Grid, Pagination } from 'swiper/modules';

function Services() {
    return (
        <div class="services-container">
            <ServicesMenu />
            <Swiper
                slidesPerView={3}
                freeMode={true}
                direction={'vertical'}
                grid={{
                    rows: 2,
                }}
                spaceBetween={30}
                modules={[FreeMode, Grid, Pagination]}
                className="content"
            >
                {detailingServices.flatMap(category =>
                category.services.map(service => (
                    <SwiperSlide key={service.name} className={'service-id-' + category.id} >
                        <div style={{ padding: '10px', textAlign: 'center', background: '#f0f0f0', borderRadius: '5px' }}>
                            <h3>{service.name}</h3>
                            <p>{service.description}</p>
                            <p>Цена: ${service.price}</p>
                            <img src={service.url} alt={service.name} style={{ maxWidth: '100%', height: 'auto' }} />
                        </div>
                    </SwiperSlide>
                ))
            )}
            </Swiper>
        </div>
    );
}

export default Services;
