import React, {useState, useEffect } from 'react';
import { Swiper, SwiperSlide } from 'swiper/react';

import './ServicesMenu.css';
import 'swiper/css';
import 'swiper/css/free-mode';
import 'swiper/css/pagination';

import menuData from "../../data/services.json";
import { FreeMode, Pagination } from 'swiper/modules';

let blockHeight;
let firstBlockHeight;

function ServicesMenu({activeIndex, setActiveIndex}) {
    const [fixed, setFixed] = useState(false);
    // const wrapperMenuRef = useRef(null);
    // const menuRef = useRef(null);

    useEffect(() => {
        const handleScroll = () => {
            // console.log("ServicesMenu", activeIndex);
            // const block = wrapperMenuRef.current;
            const block = document.querySelector(".servicesmenu-wrapper");
            const blockTop = block.getBoundingClientRect().top;
            blockHeight = block.offsetHeight;

            firstBlockHeight = document.querySelector(".header-wrapper .content").offsetHeight;
            //console.log("blockTop=" + blockTop, "firstBlockHeight=" + firstBlockHeight);
            if (blockTop < firstBlockHeight) {
                setFixed(true);
            } else {
                setFixed(false);
            }


        };

        const handleLoad = () => {
            window.addEventListener('scroll', handleScroll);
        };

        handleLoad();

        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    });

    const handleItemClick = (index) => {
        // здесь не вызывайте activeIndex как функцию
        setActiveIndex(activeIndex);
        // остальная часть обработчика остается неизменной
        const activeElement = menuRef.current.querySelector(`.servicesmenu .service-id-${index}`);
        if (activeElement) {
            const containerRect = menuRef.current.getBoundingClientRect();
            const elementRect = activeElement.getBoundingClientRect();
            const offset = elementRect.left - containerRect.left - (containerRect.width - elementRect.width) / 2;
            menuRef.current.scrollTo({
                left: menuRef.current.scrollLeft + offset,
                behavior: 'smooth'
            });
        }
    };




    return (
        <div className='servicesmenu-wrapper' ref={wrapperMenuRef}>
            <div className={"fallback"}
                style={{
                    display: fixed ? "block" : 'none',
                    height: blockHeight
                }}
            ></div>
            <div className={'servicesmenu' + (fixed ? " fixed" : "")}
                style={{
                    position: fixed ? 'fixed' : 'inherit',
                    top: fixed ? firstBlockHeight + 'px' : ''
                }}
                ref={menuRef}
            >
                <Swiper
                    slidesPerView={"auto"}
                    freeMode={true}
                    modules={[FreeMode, Pagination]}
                    pagination={{
                        clickable: true,
                      }}
                    className='content'
                >
                    {menuData.map(service => (
                        <SwiperSlide 
                            key={service.id}
                            className={`item service-id-${service.id}`}
                            onClick={() => handleItemClick(service.id)}
                        >{service.name}</SwiperSlide>
                    ))}
                </Swiper>
            </div>
        </div>
    );
}

export default ServicesMenu;
