import React, { useEffect, useState } from "react";
import Stories from "react-insta-stories";

import storiesData from "../../data/stories.json";


function MCStories() {
  const [stories, setStories] = useState([]);

  useEffect(() => {
    const updatedStories = storiesData.map(story => {
      // Создаем полный путь к файлу в папке assets
      const filePath = require(`../../assets/stories/${story.url}`).default;

      return {
        url: filePath,
        type: story.type || 'image',
        header: {
          heading: story.overlay,
          subheading: '',
          profileImage: ''  // Вы можете добавить ссылку на изображение профиля, если нужно
        }
      };
    });

    setStories(updatedStories);
  }, []);

  return (
    <div>
      <Stories
        stories={stories}
        defaultInterval={2000}
        width={"100%"}
        height={800}
        loop={true}
      />
    </div>
  );
}

export default MCStories;
