import React, { useRef, useState, useEffect } from 'react';
import ServicesMenu from '../ServicesMenu/ServicesMenu';
import './Services.css';
import detailingServices from "../../data/services.json";
const context = require.context('../../assets/services', true);

const useItemsInObserver = (options, attributeName) => {
    const containerRef = useRef(null);
    const [activeIndex, setActiveIndex] = useState(null);

    const callbackFunction = (entries) => {
        const [entry] = entries
        if (entry.isIntersecting) {
            setActiveIndex(entry.target.getAttribute(attributeName))
            console.log(entry.target.getAttribute(attributeName));
        }
    }

    useEffect(() => {
        const observer = new IntersectionObserver(callbackFunction, options)
        if (containerRef.current) observer.observe(containerRef.current)

        return () => {
            if (containerRef.current) observer.unobserve(containerRef.current)
        }
    }, [containerRef, options]);

    return [containerRef, activeIndex];
}

function Services() {
    const [containerRef, activeIndex] = useItemsInObserver({
        root: null,
        rootMargin: '0px 0px -50% 0px', // Проверяем пересечение только с верхней границей
        threshold: 0,
    }, 'service-id')

    return (
        <div className='services-wrapper'>
            <ServicesMenu />
            <div className='services'>
                {detailingServices.map((category, index) => (
                    <div key={category.id} ref={containerRef} className={`service-category${activeIndex === index ? ' active' : ''}`} service-id={category.id}>
                        <h1>{category.name}</h1>
                        <div className='content'>
                            {category.services.map(service => (
                                <div key={service.name} className='service' service-id={category.id}>
                                    <div className='content'>
                                        <img src={service.url ? context(`./Car1.jpeg`) : context(`./${service.url}`)} alt={service.name} />
                                        <h3>{service.name}</h3>
                                        <p>{service.description}</p>
                                        <p>Цена: ${service.price}</p>
                                    </div>
                                </div>
                            ))}
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default Services;
