import React, { useRef, useState, useEffect } from 'react';
import { Swiper, SwiperSlide } from 'swiper/react';
import { HashLink as Link } from 'react-router-hash-link';
import 'swiper/css';
import 'swiper/css/free-mode';
import menuData from "../../data/services.json";
import './ServicesMenu.css';
import SwiperCore, { FreeMode } from 'swiper/core';

SwiperCore.use([FreeMode]);

function ServicesMenu() {
    const [fixed, setFixed] = useState(false);
    const wrapperMenuRef = useRef(null);
    const swiperRef = useRef(null);
    const [activeIndex, setActiveIndex] = useState(null);
    const sectionRefs = useRef({});

    useEffect(() => {
        const handleScroll = () => {
            const block = wrapperMenuRef.current;
            const blockTop = block.getBoundingClientRect().top;
            const firstBlockHeight = document.querySelector(".header-wrapper .content").offsetHeight;

            if (blockTop < firstBlockHeight) {
                setFixed(true);
            } else {
                setFixed(false);
            }

            let currentSection = null;
            Object.keys(sectionRefs.current).forEach(key => {
                const section = sectionRefs.current[key];
                const rect = section.getBoundingClientRect();
                if (rect.top <= 120 && rect.bottom >= 120) {
                    currentSection = key;
                }
            });
            if (currentSection !== null && currentSection !== activeIndex) {
                setActiveIndex(Number(currentSection));
            }
        };

        const handleLoad = () => {
            window.addEventListener('scroll', handleScroll);

            const pathname = window.location.pathname;
            if (pathname !== '/') {
                const elementId = pathname.slice(1);
                const element = document.getElementById(elementId);
                if (element) window.history.replaceState(null, null, elementId);
            }
        };

        handleLoad();

        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    }, [activeIndex]);

    useEffect(() => {
        const observerOptions = {
            root: null,
            rootMargin: '-110px 0px -99% 0px',
            threshold: [0, 1],
        };

        const observer = new IntersectionObserver((entries) => {
            entries.forEach(entry => {
                if (entry.isIntersecting) {
                    const id = entry.target.getAttribute('id').split('-')[1];
                    setActiveIndex(Number(id));
                    window.history.replaceState(null, null, `/service-${id}`);
                    scrollToSlide(id);
                }
            });
        }, observerOptions);

        menuData.forEach(service => {
            const element = document.getElementById(`service-${service.id}`);
            if (element) {
                observer.observe(element);
                sectionRefs.current[service.id] = element;
            }
        });

        return () => {
            menuData.forEach(service => {
                const element = document.getElementById(`service-${service.id}`);
                if (element) {
                    observer.unobserve(element);
                }
            });
        };
    }, []);

    const scrollToSlide = (index) => {
        if (swiperRef.current && swiperRef.current.swiper) {
            const countVisibleCategories = 1;
            swiperRef.current.swiper.slideTo(index - countVisibleCategories, 1000, false);
        }
    };

    const handleClick = (index) => {
        setActiveIndex(index);
        scrollToSlide(index);
    };

    function scrollWithOffset(el) {
        const yCoordinate = el.getBoundingClientRect().top + window.pageYOffset;
        const yOffset = -100; // Adjust this offset as needed for your fixed header
        window.scrollTo({ top: yCoordinate + yOffset, behavior: 'smooth' });
    }

    return (
        <div className='servicesmenu-wrapper' ref={wrapperMenuRef}>
            <div className={`fallback ${fixed ? 'fixed' : ''}`} style={{ height: 'auto' }}></div>

            <div className={'servicesmenu' + (fixed ? ' fixed' : '')} style={{ top: fixed ? '100px' : '0px' }}>
                <div className='content'>
                    <Swiper
                        slidesPerView={'auto'}
                        freeMode={true}
                        centeredSlides={true}
                        modules={[FreeMode]}
                        ref={swiperRef}
                    >
                        {menuData.map(service => (
                            <SwiperSlide key={service.id}>
                                <Link
                                    smooth
                                    to={`#service-${service.id}`}
                                    scroll={scrollWithOffset}
                                    onClick={() => handleClick(service.id)}
                                    className={activeIndex === service.id ? 'active-service' : ''}
                                >
                                    {service.name}
                                </Link>
                            </SwiperSlide>
                        ))}
                    </Swiper>
                </div>
            </div>
        </div>
    );
}

export default ServicesMenu;
