import React, { useRef, useState, useEffect } from 'react';
import { Swiper, SwiperSlide } from 'swiper/react';
import { Link, useLocation, useParams, useNavigate } from 'react-router-dom';
import './ServicesMenu.css';
import 'swiper/css';
import 'swiper/css/free-mode';
import { FreeMode } from 'swiper/modules';
import menuData from "../../data/services.json";

function ServicesMenu() {
    const [fixed, setFixed] = useState(false);
    const wrapperMenuRef = useRef(null);
    const swiperRef = useRef(null);
    const [activeIndex, setActiveIndex] = useState(null);
    const sectionRefs = useRef({});
    const location = useLocation();
    const { serviceId } = useParams();

    useEffect(() => {
        const handleScroll = () => {
            const block = wrapperMenuRef.current;
            const blockTop = block.getBoundingClientRect().top;

            // Получаем высоту первого блока
            const headerWrapperContent = document.querySelector(".header-wrapper .content");
            const firstBlockHeight = headerWrapperContent ? headerWrapperContent.offsetHeight : 0;

            // Устанавливаем фиксацию меню
            setFixed(blockTop < firstBlockHeight);

            // Определяем текущий раздел
            let currentSection = null;
            Object.keys(sectionRefs.current).forEach(key => {
                const section = sectionRefs.current[key];
                const rect = section.getBoundingClientRect();
                if (rect.top <= firstBlockHeight && rect.bottom >= firstBlockHeight) {
                    currentSection = key;
                }
            });
            if (currentSection !== null && currentSection !== activeIndex) {
                setActiveIndex(Number(currentSection));
            }
        };

        const handleLoad = () => {
            window.addEventListener('scroll', handleScroll);
            handleScroll(); // Вызываем при загрузке страницы для обновления фиксации и текущего раздела

            // Прокрутка к разделу при загрузке страницы, если в URL указан serviceId
            if (serviceId) {
                const element = document.getElementById(`service-${serviceId}`);
                if (element) {
                    scrollWithOffset(element);
                }
            }
        };

        handleLoad();

        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    }, [serviceId]);

    useEffect(() => {
        const observerOptions = {
            root: null,
            rootMargin: '-110px 0px -99% 0px', // Проверяем пересечение только с верхней границей
            threshold: [0, 1],
        };

        const observer = new IntersectionObserver((entries) => {
            entries.forEach(entry => {
                if (entry.isIntersecting) {
                    const id = entry.target.getAttribute('id').split('-')[1];
                    setActiveIndex(Number(id));
                    // Переход к разделу через URL при прокрутке
                    if (serviceId !== id) {
                        // Используем Link для обновления URL без перезагрузки страницы
                        navigate(`/service-${id}`);
                    }
                    scrollToSlide(id);
                }
            });
        }, observerOptions);

        menuData.forEach(service => {
            const element = document.getElementById(`service-${service.id}`);
            if (element) {
                observer.observe(element);
                sectionRefs.current[service.id] = element;
            }
        });

        return () => {
            menuData.forEach(service => {
                const element = document.getElementById(`service-${service.id}`);
                if (element) {
                    observer.unobserve(element);
                }
            });
        };
    }, [serviceId]); // Обновляем при изменении serviceId

    const navigate = useNavigate(); // Используем useNavigate для программной навигации

    const scrollToSlide = (index) => {
        if (swiperRef.current && swiperRef.current.swiper) {
            const counVisibleCategorys = 1;
            swiperRef.current.swiper.slideTo(index - counVisibleCategorys, 1000, false);
        }
    };

    const handleClick = (index) => {
        setActiveIndex(index);
        scrollToSlide(index);
    };

    function scrollWithOffset(el) {
        const yCoordinate = el.getBoundingClientRect().top + window.pageYOffset;
        const yOffset = -100; // Высота шапки
        window.scrollTo({ top: yCoordinate + yOffset, behavior: 'smooth' });
    }

    return (
        <div className='servicesmenu-wrapper' ref={wrapperMenuRef}>
            <div className={`fallback ${fixed ? 'fixed' : ''}`} style={{ height: wrapperMenuRef.current?.offsetHeight }}></div>
            <div className={`servicesmenu ${fixed ? 'fixed' : ''}`} style={{ top: fixed ? '0' : '' }}>
                <div className='content'>
                    <Swiper
                        slidesPerView={'auto'}
                        freeMode={true}
                        modules={[FreeMode]}
                        centeredSlides={true}
                        centeredSlidesBounds={true}
                        ref={swiperRef}
                    >
                        {menuData.map(service => (
                            <SwiperSlide className={`item`} key={service.id}>
                                <Link
                                    to={`/service-${service.id}`}
                                    className={`${activeIndex === service.id ? 'active-service' : ''}`}
                                    onClick={() => handleClick(service.id)}
                                >
                                    {service.name}
                                </Link>
                            </SwiperSlide>
                        ))}
                    </Swiper>
                </div>
            </div>
        </div>
    );
}

export default ServicesMenu;
