import React, { useState, useEffect } from 'react';
import ServicesMenu from '../ServicesMenu/ServicesMenu';
import './Services.css';
import detailingServices from "../../data/services.json";
const context = require.context('../../assets/services', true);

function Services() {
    const [activeIndex, setActiveIndex] = useState(null);
    const [prevActiveIndex, setPrevActiveIndex] = useState(null);

    useEffect(() => {
        const observerOptions = {
            root: null,
            rootMargin: '0px 0px -100% 0px', // Проверяем пересечение только с верхней границей
            threshold: 0,
        };

        const observerCallback = (entries) => {
            entries.forEach((entry) => {
                if (entry.isIntersecting) {
                    const index = parseInt(entry.target.getAttribute('service-id'), 10);
                    if ((index === 0 && window.scrollY < entry.target.offsetTop) || (index === detailingServices.length - 1 && window.scrollY > entry.target.offsetTop + entry.target.clientHeight)) {
                        setActiveIndex(index);
                        setPrevActiveIndex(prevActiveIndex);
                    } else if ((index !== 0 && window.scrollY >= entry.target.offsetTop) || (index !== detailingServices.length - 1 && window.scrollY <= entry.target.offsetTop + entry.target.clientHeight)) {
                        setActiveIndex(index);
                        setPrevActiveIndex(activeIndex);
                    }
                    console.log(index);
                }
            });
        };

        const observer = new IntersectionObserver(observerCallback, observerOptions);

        const sections = document.querySelectorAll('.services .service-category');
        sections.forEach((section) => {
            observer.observe(section);
        });

        return () => {
            sections.forEach((section) => {
                observer.unobserve(section);
            });
        };
    }, []);

    return (
        <div className='services-wrapper'>
            <ServicesMenu />
            <div className='services'>
                {detailingServices.map((category, index) => (
                    <div key={category.id} className={`service-category ${(activeIndex === index || prevActiveIndex === index) ? 'active' : ''}`} service-id={category.id}>
                        <h1>{category.name}</h1>
                        <div className='content'>
                            {category.services.map(service => (
                                <div key={service.name} className='service' service-id={category.id}>
                                    <div className='content'>
                                        <img src={service.url ? context(`./Car1.jpeg`) : context(`./${service.url}`)} alt={service.name} />
                                        <h3>{service.name}</h3>
                                        <p>{service.description}</p>
                                        <p>Цена: ${service.price}</p>
                                    </div>
                                </div>
                            ))}
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default Services;
