import { useState, useEffect, useRef } from 'react';

function useIntersectionObserver(options, attributeName) {
    const [activeIndex, setActiveIndex] = useState(null);
    const containerRef = useRef(null);

    useEffect(() => {
        const observerCallback = (entries) => {
            let mostVisibleIndex = null;
            let maxVisibility = 0;

            entries.forEach((entry) => {
                if (entry.intersectionRatio > maxVisibility) {
                    maxVisibility = entry.intersectionRatio;
                    mostVisibleIndex = parseInt(entry.target.getAttribute(attributeName), 10);
                }
            });

            if (mostVisibleIndex !== null) {
                setActiveIndex(mostVisibleIndex);
            }
        };

        const observer = new IntersectionObserver(observerCallback, options);

        const sections = containerRef.current.querySelectorAll(`[${attributeName}]`);
        sections.forEach((section) => {
            observer.observe(section);
        });

        return () => {
            sections.forEach((section) => {
                observer.unobserve(section);
            });
        };
    }, [options, attributeName]);

    return [containerRef, activeIndex];
}

export default useIntersectionObserver;
