import React, { useState, useEffect } from 'react';
import { Swiper, SwiperSlide } from 'swiper/react';

import './ServicesMenu.css';
import 'swiper/css';
import 'swiper/css/free-mode';

import menuData from "../../data/services.json";
import { FreeMode, Pagination } from 'swiper/modules';

let blockHeight;

function ServicesMenu() {
    const [fixed, setFixed] = useState(false);

    useEffect(() => {
        const handleScroll = () => {
            const block = document.querySelector(".servicemenu-wrapper");
            const blockTop = block.getBoundingClientRect().top;
            blockHeight = block.offsetHeight;
            const viewportHeight = window.innerHeight;

            // Высота первого блока
            const firstBlockHeight = document.querySelector(".header-wrapper .content").offsetHeight; // Пример высоты первого блока
            console.log("blockTop=" + blockTop, "firstBlockHeight=" + firstBlockHeight, "blockHeight=" + blockHeight, "viewportHeight=" + viewportHeight);

            if (blockTop <= firstBlockHeight) {
                setFixed(true);
            } else {
                setFixed(false);
            }
        };

        const handleLoad = () => {
            // Добавляем обработчик события прокрутки
            window.addEventListener('scroll', handleScroll);

            // Вызываем функцию handleScroll один раз при загрузке компонента
            handleScroll();
        };

        // Ждем полной загрузки страницы
        window.addEventListener('load', handleLoad);

        // Убираем обработчики событий при размонтировании компонента
        return () => {
            window.removeEventListener('scroll', handleScroll);
            window.removeEventListener('load', handleLoad);
        };
    }, []);

    return (
        <div className='servicemenu-wrapper'>
            <div className="fallback"
                style={{
                    display: fixed ? "block" : 'none',
                    height: blockHeight
                }}
            ></div>
            <div className='servicesmenu'
                style={{
                    position: fixed ? 'fixed' : 'inherit',
                    top: fixed ? '70px' : 'none'
                }}
            >
                <Swiper
                    slidesPerView={"auto"}
                    freeMode={true}
                    modules={[FreeMode, Pagination]}
                    className='content'
                >

                    {menuData.map(service => (
                        <SwiperSlide key={service.id} className='item'>{service.name}</SwiperSlide>
                    ))}
                </Swiper>
            </div>
        </div>
    );
}

export default ServicesMenu;
